mapper.select:

SELECT count(*)
  FROM board
 WHERE board_id = #{boardId}

mapper.insert:

	INSERT INTO board
			( board_id
			 ,user_id
			 ,title
			 ,content
			 ,img_id
			 ,user_ip
			 ,create_dt
			 ,update_dt
			)
		values 	(#{boardId}
				 ,'son'
				 ,#{title}
				 ,#{content}
				 ,#{imgId}
				 ,#{userIp}
				 ,now()
				 ,now()
				)



    	SELECT LAST_INSERT_ID();


	INSERT INTO board_image
			( create_dt
			)
		VALUES (  now()
			   )



    	SELECT LAST_INSERT_ID();



    	(SELECT IFNULL(MAX(img_srno),0)+1 AS imgSrno FROM board_image_detail WHERE IMG_ID = #{imgId})




	INSERT INTO board_image_detail
			( img_id
			 ,img_srno
			 ,origin_file_name
			 ,stored_file_name
			 ,file_size
			 ,file_path
			)
		VALUES (  #{imgId}
				 ,#{imgSrno}
				 ,#{originFileName}
				 ,#{storedName}
				 ,#{fileSize}
				 ,#{filePath}
			   )

mapper.update:

    		UPDATE board
    		   SET title = #{title}
    		      ,content = #{content}
    		      ,update_dt = now()



	    		  ,img_id = null




	    		  ,img_id = #{imgId}



    		 WHERE 1=1
    		   AND board_id = #{boardId}
    		   AND idx = #{idx}

mapper.delete:

DELETE 
  FROM board
 WHERE 1=1
   and board_id = #{boardId}
   and idx = #{idx}

mapper.update:

UPDATE board
   SET view_cnt = IFNULL(view_cnt,0)+1
 WHERE 1=1
   and board_id = #{boardId}
   and idx = #{idx}
